package Turma20;

public class Repeticao2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x, soma=0;
		
		for (x=1; x<=1000; x++)
		{
			System.out.printf("\n %d",x);
			soma = soma + x;
		}
		System.out.printf("\nSoma total: %d", soma);

	}

}
